class FrontScreen(Screen):
    def __init__(self, **kwargs):
        self.image = Image(source='logo2.png')
        super().__init__(**kwargs)
        self.layout = BoxLayout(orientation='vertical')
        self.label = Label(text="Enter a state:")
        self.message_input = TextInput()
        self.submit_button = Button(text="Submit", on_press=self.submit_message)
        self.layout.add_widget(self.label)
        self.layout.add_widget(self.image)
        self.layout.add_widget(self.message_input)
        self.layout.add_widget(self.submit_button)
        self.add_widget(self.layout)

    def submit_message(self, instance):
        message = self.message_input.text
        app = App.get_running_app()
        app.message = message  
        app.root.current = 'second'  
        my_user_input = TextInput(multiline=True)


