class MyApp(App):
    def build(self):
        self.window = FrontScreen()
        self.window.cols = 4
        self.window.size_hint = (0.6, 0.7)
        self.window.pos_hint = {"center_x": 0.5, "center_y":0.5}


        self.window.add_widget(Image(source="logo2.png"))

        self.message = ""  
        self.screen_manager = ScreenManager()
        self.first_screen = FrontScreen(name='first')
        self.second_screen = StateInput(name='second')
        self.screen_manager.add_widget(self.first_screen)
        self.screen_manager.add_widget(self.second_screen)
        return self.screen_manager

if __name__ == '__main__':
    MyApp().run()